import React, { memo, useCallback } from 'react';
import Button from '../UI/Button';

const TaskItem = memo(({ 
  task, 
  onToggleStatus, 
  onEdit, 
  onDelete 
}) => {
  const handleToggleStatus = useCallback(() => {
    onToggleStatus(task._id, task.status);
  }, [task._id, task.status, onToggleStatus]);

  const handleEdit = useCallback(() => {
    onEdit(task);
  }, [task, onEdit]);

  const handleDelete = useCallback(() => {
    onDelete(task._id);
  }, [task._id, onDelete]);

  const getStatusColor = (status) => {
    switch (status) {
      case 'todo': return 'bg-gray-100 text-gray-800';
      case 'in_progress': return 'bg-warning-100 text-warning-800';
      case 'done': return 'bg-success-100 text-success-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'high': return 'bg-danger-100 text-danger-800';
      case 'medium': return 'bg-warning-100 text-warning-800';
      case 'low': return 'bg-success-100 text-success-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusText = (status) => {
    switch (status) {
      case 'todo': return '√Ä faire';
      case 'in_progress': return 'En cours';
      case 'done': return 'Termin√©';
      default: return status;
    }
  };

  const getPriorityText = (priority) => {
    switch (priority) {
      case 'high': return 'Haute';
      case 'medium': return 'Moyenne';
      case 'low': return 'Basse';
      default: return priority;
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return null;
    return new Date(dateString).toLocaleDateString('fr-FR');
  };

  return (
    <div className={`card-interactive animate-slide-in ${task.status === 'done' ? 'opacity-75' : ''}`}>
      <div className="flex items-start justify-between">
        <div className="flex-1">
          <div className="flex items-center space-x-3 mb-3">
            <h3 className={`text-lg font-semibold ${task.status === 'done' ? 'line-through text-gray-500' : 'gradient-text'}`}>
              {task.title}
            </h3>
            <span className={`px-3 py-1 rounded-full text-xs font-medium shadow-sm ${getStatusColor(task.status)}`}>
              {getStatusText(task.status)}
            </span>
            <span className={`px-3 py-1 rounded-full text-xs font-medium shadow-sm ${getPriorityColor(task.priority)}`}>
              {getPriorityText(task.priority)}
            </span>
          </div>

          {task.description && (
            <p className={`text-gray-600 mb-3 ${task.status === 'done' ? 'line-through' : ''}`}>
              {task.description}
            </p>
          )}

          <div className="flex items-center space-x-4 text-sm text-gray-500">
            {task.dueDate && (
              <span className="flex items-center">
                üìÖ √âch√©ance: {formatDate(task.dueDate)}
              </span>
            )}
            {task.createdBy && (
              <span className="flex items-center">
                üë§ Cr√©√© par: {task.createdBy.name || task.createdBy.email}
              </span>
            )}
            <span className="flex items-center">
              üïí {formatDate(task.createdAt)}
            </span>
          </div>
        </div>

        <div className="flex items-center space-x-2 ml-4">
          <Button
            variant="outline"
            size="sm"
            onClick={handleToggleStatus}
            className="whitespace-nowrap"
          >
            {task.status === 'done' ? '‚Ü∂ Rouvrir' : task.status === 'in_progress' ? '‚úì Terminer' : '‚ñ∂ Commencer'}
          </Button>
          <Button
            variant="secondary"
            size="sm"
            onClick={handleEdit}
          >
            ‚úèÔ∏è Modifier
          </Button>
          <Button
            variant="danger"
            size="sm"
            onClick={handleDelete}
          >
            üóëÔ∏è Supprimer
          </Button>
        </div>
      </div>
    </div>
  );
});

TaskItem.displayName = 'TaskItem';

export default TaskItem;
