import React, { memo, useMemo } from 'react';
import { Link } from 'react-router-dom';
import { useTasks } from '../../hooks/useTasks';
import { useAuth } from '../../contexts/AuthContext';
import Button from '../UI/Button';

const AdminDashboard = memo(() => {
  const { allTasks, loading, taskStats } = useTasks();
  const { user } = useAuth();

  const recentTasks = useMemo(() => {
    return allTasks
      .sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt))
      .slice(0, 5);
  }, [allTasks]);

  const upcomingTasks = useMemo(() => {
    const today = new Date();
    const nextWeek = new Date(today.getTime() + 7 * 24 * 60 * 60 * 1000);

    return allTasks
      .filter(task => {
        if (!task.dueDate || task.status === 'done') return false;
        const dueDate = new Date(task.dueDate);
        return dueDate >= today && dueDate <= nextWeek;
      })
      .sort((a, b) => new Date(a.dueDate) - new Date(b.dueDate))
      .slice(0, 5);
  }, [allTasks]);

  const highPriorityTasks = useMemo(() => {
    return allTasks
      .filter(task => task.priority === 'high' && task.status !== 'done')
      .sort((a, b) => new Date(a.createdAt) - new Date(b.createdAt))
      .slice(0, 5);
  }, [allTasks]);

  const getStatusColor = (status) => {
    switch (status) {
      case 'todo': return 'text-gray-600';
      case 'in_progress': return 'text-warning-600';
      case 'done': return 'text-success-600';
      default: return 'text-gray-600';
    }
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'high': return 'text-danger-600';
      case 'medium': return 'text-warning-600';
      case 'low': return 'text-success-600';
      default: return 'text-gray-600';
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('fr-FR');
  };

  if (loading) {
    return (
      <div className="flex justify-center py-8">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary-500"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* En-t√™te Admin */}
      <div className="glass-effect bg-gradient-to-r from-purple-500 to-indigo-600 rounded-2xl shadow-2xl p-8 text-white animate-bounce-in floating-animation">
        <h1 className="text-4xl font-bold mb-3 animate-fade-in">üëë Dashboard Admin</h1>
        <p className="text-purple-100 text-lg animate-slide-in">
          Bienvenue {user?.name}, g√©rez toutes les t√¢ches et les membres de votre √©quipe
        </p>
      </div>

      {/* Statistiques compl√®tes */}
